  async ngOnInit() {
      const loading = await this.loadingCtrl.create();
      try {
          loading.present();
          document.addEventListener('online', () => { this.onlineOffline = true; });
          document.addEventListener('offline', () => { this.onlineOffline = false; });
          this.loadYears();
          this.loadYearsFc();
          await this.getInsuranceCompanies();
          await this.getInsuranceTypes();
          await this.getVehicleConditions();
          this.paramSubscription = this.route.params.subscribe(
            async (params: Params) => {
              if (params["agencyVehicleId"]) {
                this.agencyVehicleId = params["agencyVehicleId"];       
                
        
              await this.getAgencyVehicleDetails(this.agencyVehicleId);                        
              await this.getVehicleVariants(this.vehicleInfo.vehicleNameId);
              await this.getVehicleFuelTypes(this.vehicleInfo.vehicleNameId);             
              await this.getVehicleBrakingSystems(this.vehicleInfo.brakingSystemId);
              await this.getVehicleWheelTypes(this.vehicleInfo.vehicleNameId);
              await this.getVehicleColors(this.vehicleInfo.vehicleNameId);
              this.vehicleSelected = true;
                console.log(this.vehicleInfo,"data");
              
                  this.vehicleForm = {
                    form1: this.createForm1(),
                    form2: this.createForm2(),
                    form3: this.createForm3()
                  };      
                loading.dismiss();
                      
              } else {
                console.log(1);
                this.vehicleForm = {
                  form1: this.createForm1(),
                  form2: this.createForm2(),
                  form3: this.createForm3()
                };
                loading.dismiss();
              };
            }
          );
        }
      catch (err) {
        loading.dismiss();
        this.toast.showToast();
      }
  }  
  async getAgencyVehicleDetails(id: number) {
    const loading = await this.loadingCtrl.create();
    loading.present();
    return new Promise((resolve, reject) => {
      this.myVehicleViewService.getAgencyVehicleDetails(id)
      .subscribe(
        async data => {
          if (data && data.status === 'SUCCESS') {           
            this.vehicleInfo = data.data;
            try {
              // Getting Vehicle Name details
              this.vehicleNameId =data.data.vehicleNameId;
              this.vehicleNameIdPrev =data.data.vehicleNameId;
              const vehicleNameObj = <VehicleNameDetails>(await this.getVehicleName(data.data.vehicleNameId));
              this.vehicleInfo.vehicleName = vehicleNameObj.name;
              this.vehicleName= vehicleNameObj.name;
              this.vehicleInfo.airBag = vehicleNameObj.airBag === 'Y' ? 'Y' : 'N';
              // Getting Insurance details
              const insuranceObj = <InsuranceDetails>(await this.getInsuranceDetail(id));
              Object.assign(this.vehicleInfo, insuranceObj);
              // Getting other all details simultaniously
              const allArr = await Promise.all([
                this.getVehicleBrand(vehicleNameObj.vehicleBrandId),
                 this.getVehicleType(vehicleNameObj.vehicleTypeId), 
                // this.getVehicleColor(data.data.vehicleColorId), 
                 this.getVehicleVariant(data.data.vehicleVariantId), 
                 this.getVehicleFuelType(data.data.fuelTypeId), 
                 this.getVehicleWheelType(data.data.wheelTypeId),
                  this.getVehicleBrakingSystem(data.data.brakingSystemId),
                 this.getVehicleCondition(data.data.vehicleConditionId),
                  this.getInsuranceCompany(insuranceObj.insuranceCompanyId), 
                  this.getInsuranceType(insuranceObj.insuranceTypeId)]);
              this.vehicleInfo.brandName = allArr[0];
              this.vehicleInfo.vehicleType = allArr[1];
             // this.vehicleInfo.vehicleColor = allArr[2];
              this.vehicleInfo.vehicleVariant = allArr[2];
              this.vehicleInfo.fuelType = allArr[3];
              this.vehicleInfo.wheelType = allArr[4];
              this.vehicleInfo.brakingSystem = allArr[5];
              this.vehicleInfo.vehicleCondition = allArr[6];
              this.vehicleInfo.insuranceCompany = allArr[7];
              this.vehicleInfo.insuranceType = allArr[8];
              this.vehicleBrand =  this.vehicleInfo.brandName;
              // Checking images
              if (this.vehicleInfo.displayImage == null || this.vehicleInfo.displayImage == undefined ) {               
                this.vehicleInfo.displayImage = 'assets/img/displayview.jpg';  
                console.log(this.vehicleInfo.displayImage,"null");
              }
              else{
                console.log(this.vehicleInfo.displayImage,"notnull");
                if (this.vehicleImages) { this.vehicleImages.push({ img: this.vehicleInfo.displayImage }); } else { this.vehicleImages = [{ img: this.vehicleInfo.displayImage }]; } 
                
         
              }
              if (this.vehicleInfo.frontImage  == null || this.vehicleInfo.frontImage == undefined) { 
                this.vehicleInfo.frontImage = 'assets/img/frontview.jpg';
              }else{
                if (this.vehicleImages) { this.vehicleImages.push({ img: this.vehicleInfo.frontImage }); } else { this.vehicleImages = [{ img: this.vehicleInfo.frontImage }]; }

               }
              if (this.vehicleInfo.backImage  == null|| this.vehicleInfo.backImage == undefined) 
              {  this.vehicleInfo.backImage = 'assets/img/backview.jpg';            }
              else{
                if (this.vehicleImages) { this.vehicleImages.push({ img: this.vehicleInfo.backImage }); } else { this.vehicleImages = [{ img: this.vehicleInfo.backImage }]; } 
             
              }
              if (this.vehicleInfo.leftImage  == null|| this.vehicleInfo.leftImage == undefined) 
              {
                 
                 this.vehicleInfo.leftImage = 'assets/img/leftview.jpg';
              }
              else{
                if (this.vehicleImages) { this.vehicleImages.push({ img: this.vehicleInfo.leftImage }); } else { this.vehicleImages = [{ img: this.vehicleInfo.leftImage }]; } 
              }
              if (this.vehicleInfo.rightImage  == null|| this.vehicleInfo.rightImage == undefined) {
                this.vehicleInfo.rightImage = 'assets/img/rightview.jpg';
              }
              else{
                if (this.vehicleImages) { this.vehicleImages.push({ img: this.vehicleInfo.rightImage }); } else { this.vehicleImages = [{ img: this.vehicleInfo.rightImage }]; } 
              }
      


           
              // backImage = 'assets/img/backview.jpg';
              // leftImage = 'assets/img/leftview.jpg';
              // rightImage = 'assets/img/rightview.jpg';
              loading.dismiss();
              console.log(this.vehicleInfo.brandName);
              resolve(1);
            } catch (err) {
              loading.dismiss();
              this.toast.showToast(err);
              reject(err);
            }
          } else {
            loading.dismiss();
            this.toast.showToast();
          }
        }, async err => {
          loading.dismiss();
          this.toast.showToast();
          reject(err);
        }
      );
    });
  }
